#!/bin/bash
# <bitbar.title>Open local git repos</bitbar.title>
# <bitbar.version>v1.0</bitbar.version>
# <bitbar.author.github>ghandic</bitbar.author.github>
# <bitbar.author>AndyChallis</bitbar.author>
# <bitbar.desc>Opens the repos folders</bitbar.desc>

if [[ "$1" = "gitlab" ]]; then
  open ~/Documents/GitLab
fi

if [[ "$1" = "github" ]]; then
  open ~/Documents/GitHub
fi

if [[ "$1" = "keybase" ]]; then
  open ~/Documents/Keybase
fi

echo "| "
echo "---"
echo "GitLab| bash='$0' param1=gitlab terminal=false refresh=true";
echo "GitHub| bash='$0' param1=github terminal=false refresh=true";
echo "KeyBase| bash='$0' param1=keybase terminal=false refresh=true";
